mapper.select:
SELECT aca_id acaId,aca_name acaName FROM academy a
where 1=1

    and a.aca_id=#{acaId}


    and a.aca_name=#{acaName}

SELECT a.aca_id acaId, a.aca_name acaName, p.profession_id professionId, p.profession_name professionName FROM profession p LEFT JOIN academy a ON p.profession_aca = a.aca_id where 1=1
and p.profession_id=#{professionId}
and p.profession_name=#{professionName}

SELECT a.aca_id acaId, a.aca_name acaName, p.profession_id professionId, p.profession_name professionName, c.c_id cId, c.c_name cName FROM class c LEFT JOIN profession p ON c.c_profession = p.profession_id LEFT JOIN academy a ON c.c_aca = a.aca_id where 1=1
and c.c_id=#{cId}
and c.c_name=#{cName}
