mapper.select:
SELECT c.cr_id crId, c.cr_name crName, a.aca_name acaName FROM course c LEFT JOIN academy a ON c.cr_aca = a.aca_id where 1=1
and c.cr_id=#{crId}
and c.cr_name=#{crName}
and a.aca_name=#{acaName}

SELECT ct.id id, c.cr_id crId, c.cr_name crName, a.aca_name acaName, t.t_name tName, ct.select_status selectStatus FROM course_teacher ct LEFT JOIN course c ON ct.cr_id = c.cr_id LEFT JOIN teacher t ON t.t_id = ct.t_id LEFT JOIN academy a ON a.aca_id = c.cr_aca where 1=1 and ct.select_status=1
and c.cr_id=#{crId}
and c.cr_name=#{crName}
and a.aca_name=#{acaName}
and t.t_name=#{tName}
and t.t_id=#{tId}

mapper.update:
update course_teacher
  set

        select_status = #{selectStatus,jdbcType=INTEGER}
      where id = #{id,jdbcType=INTEGER}

update course_student
   set

       select_status = #{selectStatus,jdbcType=INTEGER}
       where id = #{id,jdbcType=INTEGER}
