mapper.update:
update course_teacher
  set

        select_status = #{selectStatus,jdbcType=INTEGER}
      where id = #{id,jdbcType=INTEGER}

update course_student
   set

       select_status = #{selectStatus,jdbcType=INTEGER}
       where id = #{id,jdbcType=INTEGER}
