mapper.select:
SELECT hw.h_id hId, hw.h_realname hName, c.cr_id crId, c.cr_name crName, hw.h_uptime hUptime, hw.h_starttime hStarttime, hw.h_endtime hEndtime FROM home_work hw LEFT JOIN course c ON hw.h_course = c.cr_id WHERE 1=1
and hw.h_uper = #{hUper}
and hw.h_realname like concat(concat('%',#{hRealname}),'%')
and c.cr_id=#{crId}
and c.cr_name like concat(concat('%',#{crName}),'%')
and NOW() between hw.h_uptime and hw.h_starttime
and NOW() between hw.h_starttime and hw.h_endtime
AND NOW() >= hw.h_endtime
and hw.h_starttime >= #{hStarttime} and hw.h_endtime <= #{hEndtime}

SELECT hs.h_id hId, hw.h_realname hName, c.cr_id crId, c.cr_name crName, t.t_name tName, hw.h_starttime hStarttime, hw.h_endtime hEndtime, hs.h_status_stu hStatusStu, hw.h_comment hComment, hw.h_uper hUper FROM homework_student hs LEFT JOIN home_work hw ON hw.h_id = hs.h_id LEFT JOIN teacher t ON t.t_id = hw.h_uper LEFT JOIN course c ON hw.h_course = c.cr_id WHERE 1=1 and hw.h_starttime <= NOW()
and hs.s_id=#{sId}
and hs.h_id=#{hId}
and hw.h_realname like concat(concat('%',#{hRealname}),'%')
and c.cr_id=#{crId}
and c.cr_name like concat(concat('%',#{crName}),'%')
and t.t_name=#{tName}
and hs.h_status_stu=#{hStatus}

mapper.update:
update homework_student


        h_status_stu = #{hStatusStu,jdbcType=INTEGER},


where  1=1

   and s_id = #{sId}


    and h_id in

            #{hId,jdbcType=INTEGER}



    and id=#{id}
